CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `full_report` AS select `reports_list`.`report_date` AS `report_date`,`reports`.`item_id` AS `item_id`,`items`.`item_name` AS `item_name`,`units`.`unit_name` AS `unit_name`,sum(`reports`.`report_quantity`) AS `sum(report_quantity)` from (((`reports_list` join `reports` on(((`reports`.`report_id` = `reports_list`.`report_id`) and (`reports`.`workshop_id` = `reports_list`.`workshop_id`)))) join `items` on((`items`.`item_id` = `reports`.`item_id`))) join `units` on((`units`.`unit_id` = `items`.`unit_id`))) group by `reports`.`item_id`